import App from "../App";

import '../App.css';

function About(){
    return(
  <div>
 <p className="ttt">
     <b>Sharma Transports </b> is a dedicated bus operator offering travel services across India.</p> 
     <p>The company comprises of 392 buses creating convenience for travellers. </p>
     <p>  Well maintained buses and skilled drivers ensure road safety for passengers willing to travel. </p> 
     <p>  The bus company is a provider of various types of bus services connecting different city routes.
  </p>   
      
     <p>
         
     Sharma Transports has a good frequency of buses that run all throughout the day promoting safety and comfort of passengers. Sharma Transports staff are known for assisting passengers consistently. Travellers prefer Sharma Transports due to its convenient and on time bus services across various city routes.
         
         </p> 
  </div>




    )
}
export default About;
