import '../App.css';

function About(){
    return(
  <div>
 <p className="ttt">

 <u>  <b><h1>About Us</h1></b> <br/> </u>

     <b>Sharma Transports </b> is a dedicated bus operator offering travel services across India.<br/> <br/>
     The company comprises of 392 buses creating convenience for travellers. <br/> <br/>
     Well maintained buses and skilled drivers ensure road safety for passengers willing to travel. <br/> <br/> 
     The bus company is a provider of various types of bus services connecting different city routes. <br/> <br/>
     Sharma Transports staff are known for assisting passengers consistently. <br/>  <br/>Travellers prefer Sharma Transports due to its convenient and on time bus services across various city routes. <br/> <br/>
     Sharma Transports has a good frequency of buses that run all throughout the day promoting safety and comfort of passengers. 
         
         </p> 
  </div>




    )
}
export default About;
