import App from "../App";

import '../App.css';

function About(){
    return(
  <div>
 <p className="ttt">
     <b>Sharma Transports </b> is a dedicated bus operator offering travel services across India.<br/> 
     The company comprises of 392 buses creating convenience for travellers. <br/>
     Well maintained buses and skilled drivers ensure road safety for passengers willing to travel. <br/> 
     The bus company is a provider of various types of bus services connecting different city routes. <br/>
     Sharma Transports has a good frequency of buses that run all throughout the day promoting safety and comfort of passengers. <br/> Sharma Transports staff are known for assisting passengers consistently. <br/> Travellers prefer Sharma Transports due to its convenient and on time bus services across various city routes.
         
         </p> 
  </div>




    )
}
export default About;
